const add = function (num1, num2) {
  let result = num1 + num2;

  return result;
};

const subtract = function (num1, num2) {
  let result = num1 - num2;

  return result;
};

const sum = function (arr) {
  let sum = 0;

  for (i = 0; i < arr.length; i++) {
    sum += arr[i];

    return sum;

  }
}


